import { useState } from "react";

const App = () => {
    const [counterValue, setCounter] = useState(0);

    const handleClick = (action) => {
        if (action === "increase") {
            setCounter(counterValue + 1);
        } else if (action === "reset") {
            setCounter(0);
        } else if (action === "decrease") {
            setCounter(counterValue - 1);
        } else;
    };

    const reset = () => {
        setCounter(0);
    };
    const isEven = counterValue % 2 === 0 ? "es Par" : "no es Par";
    return (
        <div>
            <h1>{counterValue}</h1>
            <button
                onClick={() => {
                    handleClick(this);
                }}
            >
                increment
            </button>
            <button>Decrease</button>
            <button onClick={reset}>Reset</button>
            <br />
            <span>{isEven}</span>
        </div>
    );
};

export default App;
