import { useState } from "react";

const App = () => {
    const [counterValue, setCounter] = useState(0);

    const handleClick = (action) => {
        return action
            ? setCounter((prev) => prev + 1)
            : setCounter((prev) => prev - 1);
    };

    const isEven = counterValue % 2 === 0 ? "es Par" : "no es Par";
    return (
        <div>
            <h1>{counterValue}</h1>
            <button
                onClick={() => {
                    handleClick("increase");
                }}
            >
                increment
            </button>
            <button
                onClick={() => {
                    handleClick("decrease");
                }}
            >
                Decrease
            </button>
            <button
                onClick={() => {
                    handleClick("reset");
                }}
            >
                Reset
            </button>
            <br />
            <span>{isEven}</span>
        </div>
    );
};

export default App;
