import { useState } from "react";

const App = () => {
    const [counter, setCounter] = useState({
        left: 0,
        right: 0,
    });

    const [counterPosition, setCounterPosition] = useState([]);

    const Aviso = () => <h1>Aun no has dado click</h1>

    const CantidadClicks = ({ clicks }) => clicks

    const handleClickLeft = () => {
        const newInfo = {
            ...counter,
            left: counter.left + 1,
        };
        setCounter(newInfo);
        setCounterPosition(counterPosition.concat("L"));
    };

    const handleClickRight = () => {
        const newInfo = {
            ...counter,
            right: counter.right + 1,
        };
        setCounter(newInfo);
        setCounterPosition(counterPosition.concat("R"));
    };

    const Button = ({ handle, text, contador }) => <button onClick={handle}>{text} {contador}</button>

    const clicks = counterPosition.length;
    return (
        <div>
            <Button handl={handleClickLeft} text="left" contador={counter.left} />
            <button onClick={handleClickRight}>right {counter.right}</button>
            <br />
            {counterPosition.join(",")}
            <br />
            {clicks === 0 ? <Aviso /> : <CantidadClicks clicks={clicks} />}
        </div>
    );
};

export default App;
