export const Display = ({ countriesArr }) => {
  const countriesLength = countriesArr.length
  if (countriesLength === 1) {
    const country = countriesArr[0]
    const languages = Object.keys(country.languages)
    return (
      <div>
        <div>
          <h1>{country.name.official}</h1>
          <p>Capital: {country.capital[0]}</p>
          <p>Area: {country.area}</p>
        </div>
        <div>
          <h2>Languages:</h2>
          <ul>
            {languages.map((language) => (
              <li key={language}>{language}</li>
            ))}
          </ul>
        </div>
        <div>
          <img src={country.flags.png}></img>
        </div>
      </div>
    )
  } else if (countriesLength > 10) {
    return <p>Too many matches, specify another filter</p>
  } else if (countriesLength > 1 && countriesLength <= 10) {
    return (
      <ul>
        {countriesArr.map((country) => (
          <li key={country.name.common}>{country.name.official}</li>
        ))}
      </ul>
    )
  }
}
