import { useEffect, useState } from "react"
import axios from "axios"
export const Country = ({ country }) => {
  if (country) {
    const [weather, setWeather] = useState({})
    const languages = Object.values(country.languages)
    const capitalLatitude = country.capitalInfo.latlng[0]
    const capitalLongitude = country.capitalInfo.latlng[1]
    const climate = (lat, lon) => {
      const apiKey = process.env.REACT_APP_API_KEY
      axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&units=metric`
      )
    }
    useEffect(() => {
      climate(capitalLatitude, capitalLongitude)
        .then((response) => response.data)
        .then((weatherData) => setWeather(weatherData))
    }, [])
    if (Object.keys(weather) > 0) {
      return (
        <div>
          <div>
            <h1>{country.name.common}</h1>
            <p>Capital: {country.capital}</p>
            <p>Area: {country.area}</p>
          </div>
          <div>
            <h2>Languages:</h2>
            <ul>
              {languages.map((language) => (
                <li key={language}>{language}</li>
              ))}
            </ul>
          </div>
          <div>
            <img src={country.flags.png}></img>
          </div>
          <div>
            <h2>Wheater in {country.capital}</h2>
            <p>Temperature {weather.current.temp} Celsius</p>
            <img
              src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@4x.png`}
              alt="weather icon"
            />
            <p>Wind {weather.current.wind_speed} m/s</p>
          </div>
        </div>
      )
    }
  }
}
