import { Country } from "./Country"
import { useState } from "react"
export const Display = ({ countriesArr }) => {
  const [isShown, setIsShown] = useState(false)

  const countriesLength = countriesArr.length
  const showDisplay = () => {
    setIsShown((prevState) => !prevState)
  }

  if (countriesLength === 1) {
    const country = countriesArr[0]
    return <Country country={country} />
  } else if (countriesLength > 10) {
    return <p>Too many matches, specify another filter</p>
  } else if (countriesLength > 1 && countriesLength <= 10) {
    return (
      <ul>
        {countriesArr.map((pais) => (
          <div key={pais.name.common}>
            <li>{pais.name.official}</li>
            <button type="button" onClick={showDisplay}>
              {isShown ? "hide" : "show"}
            </button>
            {isShown ? <Display country={pais} /> : null}
          </div>
        ))}
      </ul>
    )
  }
}
